import React, { Component } from 'react';
import Image2 from '../Image/images.jpg';
import { Link, useNavigate } from 'react-router-dom';  
import '../Add-Department/AddDepartment.css';

// Inline style definitions
const background = { backgroundSize: 'cover' };
const texts = { position: 'absolute' };

// Higher Order Component (HOC) to use hooks inside a class component
function withRouter(Component) {
    function ComponentWithRouterProp(props) {
        const navigate = useNavigate();
        return <Component {...props} navigate={navigate} />;
    }
    return ComponentWithRouterProp;
}

class AddMessage extends Component {
    constructor() {
        super();
        this.state = {
            ids: 0,
            message: ''
        };
    }

    // Handles form submission
    onFormSubmitted = (e) => {
        e.preventDefault();
        this.props.addsubscriberHandler(this.state);  // Calls parent handler to add message
        this.setState({ ids: 0, message: '' });  // Reset state after submission
        this.props.navigate("/");  // Redirect to the homepage using navigate
    }

    // Handles input changes and updates state
    inputChangedHandler = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    render() {
        return (
            <div className="main">
                <img style={background} className="images" height="100%" width="100%" src={Image2} alt="background" />
                <form style={texts} onSubmit={this.onFormSubmitted}>
                    <Link to="/">
                        <button type="button" className="back_btn">Back</button>
                    </Link>
                    <br />
                    <label htmlFor="message" className="label-control">MESSAGE: </label><br />
                    <input
                        id="message"
                        type="text"
                        className="input-control"
                        name="message"
                        placeholder="Enter New Message"
                        value={this.state.message}
                        onChange={this.inputChangedHandler}
                    />
                    <br /><br />
                    <button type="submit" className="add">Add</button>
                </form>
            </div>
        );
    }
}

// Wrap the class component with withRouter HOC to use navigate prop
export default withRouter(AddMessage);
