import React, { Component } from 'react';
import Image from '../Image/images.jpg';
import Navigation from '../Navigation/Navigation.js';
import './MyMessages.css';
import { Link } from 'react-router-dom';
import { VOContainer, VOHeader, VOMain, VOButton, VOGrid, VOGridItem, VOText, VOImage } from '@vercel/vo'; // Import VO Components

class MyMessage extends Component {
  onDeletedClick = (subscriberId) => {
    this.props.deleteSubscriberHandler(subscriberId);
  };

  render() {
    return (
      <VOContainer>
        {/* VOHeader for the navigation bar */}
        <VOHeader>
          <Navigation />
        </VOHeader>

        {/* Background image for the message section */}
        <VOMain>
          <VOImage
            src={Image}
            alt="Background"
            style={{ width: '100%', height: 'auto', position: 'relative', backgroundSize: 'cover' }}
          />

          {/* Section title */}
          <div style={{ position: 'absolute', top: '5%', left: '5%', color: 'white' }}>
            <h1>Important Information regarding College</h1>
          </div>

          {/* Message list using VOGrid for better layout */}
          <div style={{ position: 'relative', top: '10%', color: 'white' }}>
            <VOGrid columns={3} className="grid-header">
              <VOGridItem><VOText>S.No.</VOText></VOGridItem>
              <VOGridItem><VOText>MESSAGE</VOText></VOGridItem>
              <VOGridItem><VOText>ACTION</VOText></VOGridItem>
            </VOGrid>

            {/* Dynamic message list */}
            {this.props.DepartmentList.map((sub, index) => (
              <VOGrid key={sub.ids} columns={3} className="grid-row">
                <VOGridItem><VOText>{index + 1}</VOText></VOGridItem>
                <VOGridItem><VOText>{sub.message}</VOText></VOGridItem>
                <VOGridItem>
                  <VOButton onClick={() => this.onDeletedClick(sub.ids)} className="delete-btn">Delete</VOButton>
                </VOGridItem>
              </VOGrid>
            ))}

            {/* Add new message button */}
            <Link to="/add">
              <VOButton className="custom-btn add-btn">Add Message</VOButton>
            </Link>
          </div>
        </VOMain>
      </VOContainer>
    );
  }
}

export default MyMessage;
