import React, { Component } from 'react';
import Image2 from '../Image/images.jpg';
import { Link, useNavigate } from 'react-router-dom';
import { VOContainer, VOForm, VOButton, VOInput, VOImage } from '@vercel/vo'; // Importing Vercel's VO components
import '../Add-Department/AddDepartment.css';

// Higher-order component to inject navigate function as a prop
function withRouter(Component) {
    function ComponentWithRouterProp(props) {
        const navigate = useNavigate();
        return <Component {...props} navigate={navigate} />;
    }
    return ComponentWithRouterProp;
}

class AddMessage extends Component {
    constructor() {
        super();
        this.state = {
            ids: 0,
            message: ''
        };
    }

    onFormSubmitted = (e) => {
        e.preventDefault();
        this.props.addsubscriberHandler(this.state);
        this.setState({ ids: 0, message: '' });
        this.props.navigate("/");
    }

    inputChangedHandler = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    render() {
        return (
            <VOContainer>
                <VOImage src={Image2} alt="background" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                <VOForm onSubmit={this.onFormSubmitted} style={{ position: 'absolute', top: '20%', left: '10%' }}>
                    <Link to="/">
                        <VOButton type="button" className="back_btn">Back</VOButton>
                    </Link>
                    <br />
                    <label htmlFor="message" className="label-control">MESSAGE: </label><br />
                    <VOInput
                        id="message"
                        type="text"
                        name="message"
                        placeholder="Enter New Message"
                        value={this.state.message}
                        onChange={this.inputChangedHandler}
                        required
                    />
                    <br /><br />
                    <VOButton type="submit" className="add">Add</VOButton>
                </VOForm>
            </VOContainer>
        );
    }
}

export default withRouter(AddMessage);
