// src/redux/actions.js
export const SET_STUDENTS = 'SET_STUDENTS';
export const SET_COURSES = 'SET_COURSES';
export const SET_FACULTY = 'SET_FACULTY';

export const setStudents = (students) => ({ type: SET_STUDENTS, payload: students });
export const setCourses = (courses) => ({ type: SET_COURSES, payload: courses });
export const setFaculty = (faculty) => ({ type: SET_FACULTY, payload: faculty });
