import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AddSection from './AddSection';
import Section from '../Bar-contents/Section';
import { VOContainer } from '@vercel/vo';  // VOContainer for layout structure

const ShowSection = () => {
    const [departmentList, setDepartmentList] = useState([
        { ids: 1, sections: 'A', year: '2023' },
        { ids: 2, sections: 'B', year: '2024' }
    ]);

    const deleteSubscriberHandler = (subscriberId) => {
        setDepartmentList(prevList => prevList.filter(subscriber => subscriber.ids !== subscriberId));
    };

    const addSubscriberHandler = (newSubscriber) => {
        setDepartmentList(prevList => {
            const newId = prevList.length > 0 ? prevList[prevList.length - 1].ids + 1 : 1;
            return [...prevList, { ...newSubscriber, ids: newId }];
        });
    };

    return (
        <VOContainer>
            <Router>
                <Routes>
                    <Route
                        path='/'
                        element={<Section departmentList={departmentList} deleteSubscriberHandler={deleteSubscriberHandler} />}
                    />
                    <Route
                        path='/add'
                        element={<AddSection addSubscriberHandler={addSubscriberHandler} />}
                    />
                </Routes>
            </Router>
        </VOContainer>
    );
};

export default ShowSection;
