import React, { Component } from 'react';
import Image2 from '../Image/50-Beautiful-and-Minimalist-Presentation-Backgrounds-042.jpg';
import './Departments.css';
import { Link } from 'react-router-dom';
import Navigation from '../Navigation/Navigation';
import { VOContainer, VOHeader, VOMain, VOButton, VOGrid, VOGridItem, VOText, VOImage } from '@vercel/vo'; // Import VO Components

class Section extends Component {
  onDeletedClick = (subscriberId) => {
    this.props.deleteSubscriberHandler(subscriberId);
  };

  render() {
    return (
      <VOContainer>
        {/* VOHeader for the top navigation bar */}
        <VOHeader>
          <Navigation />
        </VOHeader>

        {/* Background image for the section */}
        <VOMain>
          <VOImage
            src={Image2}
            alt="Background"
            style={{ width: '100%', height: 'auto', position: 'relative', backgroundSize: 'cover' }}
          />
          
          {/* Section title */}
          <div style={{ position: 'absolute', top: '5%', left: '5%', color: 'white', textDecoration: 'underline' }}>
            <h1>Add New Section</h1>
          </div>

          {/* Section list using VOGrid for better layout */}
          <div style={{ position: 'relative', top: '10%', color: 'white' }}>
            <VOGrid columns={4} className="grid-header">
              <VOGridItem>
                <VOText>S.No.</VOText>
              </VOGridItem>
              <VOGridItem>
                <VOText>SECTION</VOText>
              </VOGridItem>
              <VOGridItem>
                <VOText>YEAR</VOText>
              </VOGridItem>
              <VOGridItem>
                <VOText>ACTION</VOText>
              </VOGridItem>
            </VOGrid>

            {/* Dynamic section list */}
            {this.props.DepartmentList.map((sub, index) => (
              <VOGrid key={sub.ids} columns={4} className="grid-row">
                <VOGridItem>
                  <VOText>{index + 1}</VOText>
                </VOGridItem>
                <VOGridItem>
                  <VOText>{sub.sections}</VOText>
                </VOGridItem>
                <VOGridItem>
                  <VOText>{sub.year}</VOText>
                </VOGridItem>
                <VOGridItem>
                  <VOButton onClick={() => this.onDeletedClick(sub.ids)} className="delete-btn">
                    Delete
                  </VOButton>
                </VOGridItem>
              </VOGrid>
            ))}

            {/* Add new section button */}
            <Link to="/add">
              <VOButton className="custom-btn add-btn">Add Section</VOButton>
            </Link>
          </div>
        </VOMain>
      </VOContainer>
    );
  }
}

export default Section;
