import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Student from '../Bar-contents/Student';
import AddStudent from './AddStudent';

class ShowStudent extends Component {
    constructor() {
        super();
        this.state = {
            DepartmentList: [
                {
                    ids: 1,
                    Reg: '11330',
                    name: 'Rohit',
                    phone: '9999999999',
                    branch: 'cse'
                },
                {
                    ids: 2,
                    Reg: '13838',
                    name: 'Rohan',
                    phone: '777777777',
                    branch: 'cse'
                }
            ]
        };
    }

    deleteSubscriberHandler = (subscriberId) => {
        this.setState(prevState => ({
            DepartmentList: prevState.DepartmentList.filter(subscriber => subscriber.ids !== subscriberId)
        }));
    }

    addSubscriberHandler = (newSubscriber) => {
        this.setState(prevState => {
            const newId = prevState.DepartmentList.length > 0
                ? prevState.DepartmentList[prevState.DepartmentList.length - 1].ids + 1
                : 1;
            return {
                DepartmentList: [...prevState.DepartmentList, { ...newSubscriber, ids: newId }]
            };
        });
    }

    render() {
        return (
            <Router>
                <Routes>
                    <Route 
                        path="/" 
                        element={<Student DepartmentList={this.state.DepartmentList} deleteSubscriberHandler={this.deleteSubscriberHandler} />} 
                    />
                    <Route 
                        path="/add" 
                        element={<AddStudent addSubscriberHandler={this.addSubscriberHandler} />} 
                    />
                </Routes>
            </Router>
        );
    }
}

export default ShowStudent;
