import React, { useState } from 'react';
import { VOButton, VOInput, VOContainer } from '@vercel/vo';  // Using Vercel's VO components
import { Link, useNavigate } from 'react-router-dom';
import '../Add-Department/AddDepartment.css';  // Keep your CSS for custom styling

const AddSection = ({ addSubscriberHandler }) => {
    const [sectionData, setSectionData] = useState({ sections: '', year: '' });
    const navigate = useNavigate();

    const onFormSubmitted = (e) => {
        e.preventDefault();
        addSubscriberHandler(sectionData);
        setSectionData({ sections: '', year: '' });
        navigate("/");
    };

    const inputChangedHandler = (e) => {
        const { name, value } = e.target;
        setSectionData(prevState => ({ ...prevState, [name]: value }));
    };

    return (
        <VOContainer center className="main-container">
            <form onSubmit={onFormSubmitted}>
                <Link to="/">
                    <VOButton className="back_btn" type="button">
                        Back
                    </VOButton>
                </Link>
                <br />
                <VOInput
                    id="sections"
                    name="sections"
                    label="Section"
                    placeholder="Enter New Section"
                    value={sectionData.sections}
                    onChange={inputChangedHandler}
                    required
                />
                <br /><br />
                <VOInput
                    id="year"
                    name="year"
                    label="Year"
                    placeholder="Enter Year"
                    value={sectionData.year}
                    onChange={inputChangedHandler}
                    required
                />
                <br /><br />
                <VOButton className="add-btn" type="submit">
                    Add
                </VOButton>
            </form>
        </VOContainer>
    );
};

export default AddSection;
