import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Updated to use Routes
import MyMessage from '../Bar-contents/MyMessages';
import AddMessage from './AddMessage';

class ShowMessage extends Component {
  constructor() {
    super();
    this.state = {
      DepartmentList: [
        { ids: 1, message: 'Hello, Good Morning' },
        { ids: 2, message: 'I am Prateek' }
      ]
    };
  }

  deleteSubscriberHandler = (subscriberId) => {
    this.setState(prevState => ({
      DepartmentList: prevState.DepartmentList.filter(subscriber => subscriber.ids !== subscriberId)
    }));
  };

  addSubscriberHandler = (newSubscriber) => {
    this.setState(prevState => {
      const newId = prevState.DepartmentList.length > 0
        ? prevState.DepartmentList[prevState.DepartmentList.length - 1].ids + 1
        : 1;
      return {
        DepartmentList: [
          ...prevState.DepartmentList,
          { ...newSubscriber, ids: newId }
        ]
      };
    });
  };

  render() {
    return (
      <Router>
        <Routes>
          <Route
            path='/'
            element={
              <MyMessage
                DepartmentList={this.state.DepartmentList}
                deleteSubscriberHandler={this.deleteSubscriberHandler}
              />
            }
          />
          <Route
            path='/add'
            element={<AddMessage addSubscriberHandler={this.addSubscriberHandler} />}
          />
        </Routes>
      </Router>
    );
  }
}

export default ShowMessage;
