import React, { Component } from 'react';
import Image2 from '../Image/50-Beautiful-and-Minimalist-Presentation-Backgrounds-042.jpg';
import { Link } from 'react-router-dom';
import Navigation from '../Navigation/Navigation';
import { VOContainer, VOMain, VOButton, VOGrid, VOGridItem, VOText, VOImage, VOHeader } from '@vercel/vo'; // Import VO Components
import './Departments.css';

class Department extends Component {
  onDeletedClick = (subscriberId) => {
    this.props.deleteSubscriberHandler(subscriberId);
  };

  render() {
    return (
      <VOContainer>
        {/* Navigation Section */}
        <VOHeader>
          <Navigation />
        </VOHeader>

        {/* Background Image Section */}
        <VOMain>
          <VOImage
            src={Image2}
            alt="Background"
            style={{ width: '100%', height: 'auto', backgroundSize: 'cover', position: 'relative' }}
          />

          {/* Title */}
          <div style={{ position: 'absolute', top: '10%', left: '5%', color: 'white' }}>
            <h1>Add New Department Section</h1>
          </div>

          {/* Department List */}
          <div style={{ position: 'relative', top: '15%', color: 'white' }}>
            <VOGrid columns={4} className="grid-header">
              <VOGridItem><VOText>S.No.</VOText></VOGridItem>
              <VOGridItem><VOText>NAME / PHONE</VOText></VOGridItem>
              <VOGridItem><VOText>REGISTRATION NO.</VOText></VOGridItem>
              <VOGridItem><VO>BRANCH</VO></VOGridItem>
            </VOGrid>

            {/* Mapping Department List */}
            {this.props.DepartmentList.map((sub, index) => (
              <VOGrid key={sub.ids} columns={4} className="grid-row">
                <VOGridItem><VOText>{index + 1}</VOText></VOGridItem>
                <VOGridItem><VOText>{sub.name} / {sub.phone}</VOText></VOGridItem>
                <VOGridItem><VOText>{sub.Reg}</VOText></VOGridItem>
                <VOGridItem><VOText>{sub.branch}</VOText></VOGridItem>
                <VOGridItem>
                  <VOButton className="delete-btn" onClick={() => this.onDeletedClick(sub.ids)}>Delete</VOButton>
                </VOGridItem>
              </VOGrid>
            ))}

            {/* Add New Department Button */}
            <Link to="/add">
              <VOButton className="custom-btn add-btn">Add New Department</VOButton>
            </Link>
          </div>
        </VOMain>
      </VOContainer>
    );
  }
}

export default Department;
