import React, { Component } from 'react';
import Department from '../Bar-contents/Departments';
import AddDepartment from './AddDepartment';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

class ShowDepartment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            DepartmentList: [
                {
                    ids: 1,
                    Reg: '11382',
                    name: 'Rohit',
                    phone: '9999999999',
                    branch: 'cse'
                },
                {
                    ids: 2,
                    Reg: '11783',
                    name: 'Rohan',
                    phone: '777777777',
                    branch: 'cse'
                }
            ]
        };
    }

    deleteSubscriberHandler = (subscriberId) => {
        const { DepartmentList } = this.state;
        const subscriberIndex = DepartmentList.findIndex(subscriber => subscriber.ids === subscriberId);
        if (subscriberIndex !== -1) {
            const newSubscriberList = DepartmentList.filter((_, index) => index !== subscriberIndex);
            this.setState({ DepartmentList: newSubscriberList });
        }
    }

    addSubscriberHandler = (newSubscriber) => {
        const { DepartmentList } = this.state;
        newSubscriber.ids = DepartmentList.length > 0 ? DepartmentList[DepartmentList.length - 1].ids + 1 : 1;
        this.setState({ DepartmentList: [...DepartmentList, newSubscriber] });
    }

    render() {
        return (
            <Router>
                <Routes>
                    <Route
                        path='/'
                        element={
                            <Department 
                                DepartmentList={this.state.DepartmentList} 
                                deleteSubscriberHandler={this.deleteSubscriberHandler} 
                            />
                        }
                    />
                    <Route
                        path='/add'
                        element={
                            <AddDepartment 
                                addSubscriberHandler={this.addSubscriberHandler} 
                            />
                        }
                    />
                </Routes>
            </Router>
        );
    }
}

export default ShowDepartment;
